import React from "react";
// import { BrowserRouter as Router } from "react-router-dom";
import {useState, useFetch} from "react-router-dom";
import { Routes, Route, NavLink, Outlet } from "react-router-dom";

function Home() {Z
  return (
    <main className="homepage">
      <h1>Hola!!</h1>
      <p>Welcome to my Webpage 🙏🏿🙏🏿</p>
      <p>
        Click the link below to contact me if you have any questions or would
        like to connect.
      </p>
      <NavLink
        // style={({ isActive }) =>
        //   isActive ? { color: "red" } : { color: "black" }
        // }
        className="navigate"
        to="/"
      >
        Home Page
      </NavLink>
      <NavLink className="navigate" to="/blog">
        Read my Blog
      </NavLink>
      <NavLink className="navigate" to="users">Our Users</NavLink>
      <NavLink className="navigate" to="/contact">
        Contact page
      </NavLink>
    </main>
  );
}

function Blog() {
  return (
    <main className="blog">
      <h1>You can check out some of our recipees and food blogs here👇!</h1>
      <p className="like">PS: Be sure to leave a heart if you like them🥰🥰🥰</p>
      <NavLink
        className="navigate"
        to="/"
      >
        Home Page
      </NavLink>
      <NavLink className="navigate" to="/blog">
        Read my Blog
      </NavLink>
      <NavLink className="navigate" to="/users">Our Users</NavLink>
      <NavLink className="navigate" to="/contact">
        Contact page
      </NavLink>
    </main>
  );
}

function Users() {
  return (
    <main className="users">
      <h1>Our recipes have been tested and are trusted by many popular chefs, in Nigeria and beyond. Check out some of our users here!</h1>




      <NavLink
        className="navigate"
        to="/"
      >
        Home Page
      </NavLink>
      <NavLink className="navigate" to="/blog">
        Read my Blog
      </NavLink>
      <NavLink className="navigate" to="/users">Our Users</NavLink>
      <NavLink className="navigate" to="/contact">
        Contact page
      </NavLink>
    </main>
  );
}

function Contact() {
  return (
    <main className="contact-me">
      <h1> Contact me</h1>
      <p className="contact">Fill the form below to reach out to me.</p>

      <NavLink
        className="navigate"
        to="/"
      >
        Home Page
      </NavLink>
      <NavLink className="navigate" to="/blog">
        Read my Blog
      </NavLink>
      <NavLink className="navigate" to="/users">Our Users</NavLink>
      <NavLink className="navigate" to="/contact">
        Contact page
      </NavLink>
      <NavLink className="navigate" to="/contact/signup">
        Sign Up
      </NavLink>
      <div>
        <Outlet />
      </div>
    </main>
  );
}

function SignUp() {
  return (
    <main className="sign-up">
      <h1> Sign up to my weekly newsletter</h1>
    </main>
  );
}

function NotFound() {
  return (
    <div className="notFound">
      <h1>404!</h1>
      <h1>Oops, you seem to be lost!</h1>
      <p>Here are some useful links:</p>
      <NavLink to="/" className="navigate">
        Home
      </NavLink>
      <NavLink to="/contact" className="navigate">
        Contact
      </NavLink>
      <NavLink className="navigate" to="users">Our Users</NavLink>
      <NavLink to="/contact/signup" className="navigate">
        Signup
      </NavLink>
    </div>
  );
}

function App() {
  const [page, setPage] = useState(1);
  const { loading, error, data } = useFetch(
    `https://randomuser.me/api/?page=${page}&results=10&seed=abc`
  );
  // const { loading, error, data } = useFetch(
  //   `https://randomuser.me/api/?results=1000&seed=abc`
  // );

  console.log({ loading, error, data });

  // step1
  const PER_PAGE = 5;
  // step2
  const total = data?.results?.length;
  // step3
  const pages = 50;
  // let page = 1
  // Magic of pagination
  // 1 * 10 - 10 = 0
  // 2 * 10 - 10 = 10
  // 3 * 10 - 10 = 20
  // 1 * 5 - 5 = 0
  // 2 * 5 - 5 = 5
  // 3 * 5 - 5 = 10
  const skip = page * PER_PAGE - PER_PAGE;
  // console.log(skip, total);

  // use the useEffect to make api call based on the page.

  if (loading) {
    return <>Loading...</>;
  }

  if (!loading && error) {
    return <>Error</>;
  }

  return (
    <div className="App">
      <h1 className="title">List of Users</h1>
      {/* TODO: another magic with Array.slice() */}
      {/* slice(0, 10) */}
      {/* slice(10, 20) */}
      {/* slice(20, 30) */}

      {/* 0, 0 + 5 slice(0, 5)*/}
      {/* 10, 20 */}
      {/* 20, 30 */}

      {/* 0, 1*10 */}
      {/* 10, 20 */}
      {/* 20, 30 */}

      {data?.results
        // .slice(skip, skip + PER_PAGE)
        // .slice((page - 1) * PER_PAGE, page * PER_PAGE)
        .map((each, index) => {
          const name = `${each.name.title} ${each.name.first} ${each.name.last}`;
          return (
            <li key={name.toLowerCase().replaceAll(' ', '')}>{`${
              index + 1
            }.${name}`}</li>
          );
        })}
      {
        <button
          disabled={page <= 1}
          onClick={() => setPage((prev) => prev - 1)}
        >
          prev
        </button>
      }
      <p className="pagination">
        Pages: {page} of {pages}
      </p>
      {
        <button
          disabled={page >= pages}
          aria-disabled={page >= pages}
          onClick={() => setPage((prev) => prev + 1)}
        >
          next
        </button>
      }

      {Array.from({ length: pages }, (value, index) => index + 1).map(
        (each) => (
          <button onClick={() => setPage(each)}>{each}</button>
        )
      )}
    </div>
  );



  return (
    <main className="main-container">
      {/* <Router> */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/blog" element={<Blog />} />
        <Route path="/contact" element={<Contact />}>
          <Route path="signup" element={<SignUp />} />
        </Route>
        <Route path="/users" element={<Users />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
      {/* </Router> */}
    </main>
  );
}
export default App;
