import React from "react";
// import { BrowserRouter as Router } from "react-router-dom";
import { Routes, Route, NavLink, Outlet } from "react-router-dom";

function Home() {
  return (
    <main className="homepage">
      <h1>Hola!!</h1>
      <p>Welcome to my Webpage</p>
      <p>Click the link below to contact me</p>
      <NavLink
        // style={({ isActive }) =>
        //   isActive ? { color: "red" } : { color: "black" }
        // }
       className="navigate"
       to="/"
      >
        Home Page
      </NavLink>
      <NavLink className="navigate" to="/contact">
        Contact page
      </NavLink>
    </main>
  );
}

function Blog(){
  <main className="blog">

  <h1>You can read some of my articles here!</h1>
     <NavLink
        // style={({ isActive }) =>
        //   isActive ? { color: "red" } : { color: "black" }
        // }
       className="navigate"
       to="/"
      >
        Home Page
      </NavLink>
      <NavLink className="navigate" to="/contact">
        Contact page
      </NavLink>

    </main>
}

function Contact() {
  return (
    <main className="contact-me">
      <h1> Contact me here</h1>

      <NavLink
      //   style={({ isActive }) =>
      //     isActive ? { color: "red" } : { color: "black" }
      //   }
       className="navigate"
        to="/"
       >Home Page
      </NavLink>
      <NavLink className="navigate" to="/contact">
        Contact page
      </NavLink>
      <NavLink className="navigate" to="/contact/signup">
        Sign Up
      </NavLink>
      <div>
        <Outlet />
      </div>
    </main>
  );
}

function SignUp() {
  return (
    <main className="sign-up">
      <h1> Sign up to my weekly newsletter</h1>
    </main>
  );
}

function NotFound() {
  return (
    <div className="notFound">
      <h1>404!</h1>
      <h1>Oops, you seem to be lost!</h1>
      <p>Here are some useful links:</p>
      <NavLink to="/" className="navigate">Home</NavLink>
      <NavLink to="/contact" className="navigate">Contact</NavLink>
      <NavLink to="/contact/signup" className="navigate">Signup</NavLink>
    </div>
  );
}


function App() {
  return (
    <main className="main-container">
      {/* <Router> */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/contact" element={<Contact />}>
          <Route path="signup" element={<SignUp />}/>

        </Route>
        <Route path='*' element={<NotFound />}/>
      </Routes>
      {/* </Router> */}


    </main>
  );
}
export default App;
